mapper.select:
		select cs.*,
		c.user_phone as c_user_phone,
		b.user_name as b_user_name,
		ucr.name_m as ucr_name_m
		from 
		consultation_sheet cs,user_c c, user_b b, user_c_ref ucr
		where 1=1
		and cs.user_c_no = c.user_no and cs.user_b_no = b.user_no and cs.user_ref_id=ucr.id

			AND c.user_phone = #{paramMap.user_c_phone}


			AND cs.odd_num = #{paramMap.odd_num}


			AND cs.status = #{paramMap.status}



              AND DATE_FORMAT(cs.booking_time,'%Y-%m-%d')  >=  #{paramMap.start_booking_time} 




              AND DATE_FORMAT(cs.booking_time,'%Y-%m-%d')  <=  #{paramMap.end_booking_time} 


		order by cs.CREATED_TIME desc
		limit #{startIndex}, #{numPerPage}

	    select count(*) from consultation_sheet cs,user_c c
		where 1=1
		and cs.user_c_no = c.user_no


			AND c.user_phone = #{paramMap.user_c_phone}


			AND cs.odd_num = #{paramMap.odd_num}


			AND cs.status = #{paramMap.status}



              AND DATE_FORMAT(cs.booking_time,'%Y-%m-%d')  >=  #{paramMap.start_booking_time} 




              AND DATE_FORMAT(cs.booking_time,'%Y-%m-%d')  <=  #{paramMap.end_booking_time}
